class PersistanceManager {

    urlbackend = "http://localhost:8000/Gestionale/test-gestionalejs/php/testConnection.php"; // da usare quando si usa npm start
    //urlbackend = "http://localhost/php/testConnection.php"; // da usare quando si fa una build

    static async doGet (request_body, rs) {
        const response = await fetch(this.urlbackend, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
            data: request_body,
        });
        const data = await response.text();
        rs.handle(data);
    }
}

export default PersistanceManager;
