//import LogicManager from "../businesslogic/LogicManager";
import {Alert} from '@mui/material';
//import CheckIcon from '@mui/icons-material/Check';
import '../css/error/error.css' 
//import { useState, useEffect } from "react";


const Error = ({ message, setError }) => {
    //const [message, setMessage] = useState([]);
    /*
    const refresh = () => {
        setError(prev => prev.slice(1));
    }

    useEffect(() => {
        const newError = LogicManager.getInstance().getExceptionManager().getError();
        if (newError !== ""){
            setMessage(prev => [...prev, newError]);
        }
    }, [])*/
    // let message = LogicManager.getInstance().getExceptionManager().getErrorList();
    let i = 0;  

    return (<div className="error-container">
        {message.length && message.map(item => 
            <Alert key={i++} onClick={() => {
                setError(-1);
            }} severity="error">
                {item + i}
            </Alert>) 
        }
    </div>)
}

export default Error;
