import LogicManager from "../businesslogic/LogicManager";
import React from "react";
import { Alert } from "@mui/material";
import CheckIcon from "@mui/icons-material/Check";

const Error = () => {
    const messageList = LogicManager.getInstance().getExceptionManager().getErrorList();

    return (
        <div className="error-container">
            {messageList.map((item, index) => (
                <Alert 
                    key={index} 
                    icon={<CheckIcon fontSize="inherit" />} 
                    severity="error" // Cambiato a "error" per mantenere la grafica di default
                    sx={{ mb: 1 }} // Aggiunge margine inferiore tra gli alert
                >
                    {item}
                </Alert>
            ))}
        </div>
    );
};

export default Error;
