class PersistanceManager {

    // urlbackend = "http://localhost:8000/test-gestionalejs/testConnection.php"; // da usare quando si usa npm start
    urlbackend = "http://localhost/php/testConnection.php"; // da usare quando si fa una build

    static async doGet (rs) {
        const response = await fetch(this.urlbackend, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });
        const data = await response.json();
        rs.handle(data);
    }
}

export default PersistanceManager;
