import LogicManager from "../businesslogic/LogicManager";
import { useState, useEffect } from "react";


const UserInfoModal = ({ id, setModalVisible }) => {
    const [userInfo, setUserInfo] = useState();
    const [modifica, setModifica] = useState(false);

    const updateUserInfo = async () => {
        // modifica le informazioni dell'utente
        console.log('informazioni modificate');
        
    }

    useEffect(() => {
        const loadInfo = async () => {
            const ret = LogicManager.getInstance().getUserManager().loadUserInfo(id);

            if (ret) {
                setUserInfo(ret);
            }
        }

        loadInfo();
    }, []);

    console.log(userInfo)

    return (
        <div className='userinfo-container'>
            <div className="userinfo-title">
                {userInfo ? userInfo.getUserName() : "Caricamento..."}
            </div>
            <div className="user-card">
                <div className="user-info">
                    <p>Email: </p>
                    <p>{userInfo ? userInfo.getEmail() : "Caricamento..."}</p>
                </div>
                <div className="user-info">
                    <p>Ruolo utente: </p>
                    <p>{userInfo ? (userInfo.getIsAdmin() ? 'Amministratore' : 'Utente') : "Caricamento..."}</p>
                </div>
                <div className="user-info">
                    <p>Stato utente: </p>
                    <p>{userInfo ? (userInfo.getAttivato() ? 'Attivo' : 'Disattivato') : "Caricamento..."}</p>
                </div>
            </div>
            <div className='button-container'>
                <div className='cancel-btn' onClick={() => setModalVisible(false)}>Annulla</div>
                <div className='confirm-btn' onClick={() => {
                    if (modifica) {
                        updateUserInfo();
                    }
                    else {
                        setModifica(true);
                    }
                }}>{modifica ? 'Salva' : 'Modifica'}</div>
            </div>
        </div>
    )
}

export default UserInfoModal;
