import "../css/login/login.css";
import {TextField, Button } from '@mui/material';
// import PersistanceManager from "../persistance/PersistanceManager";
import { useState } from "react";
import PersistanceManager from "../persistance/PersistanceManager";

const Login = () => {

    const [username, setUsername] = useState("");
    const [passwd, setPasswd] = useState("");

    const testConnection = async () => {
        const data = {username: username, password: passwd};

        const ret = await PersistanceManager.doPost(data, 'login.php');
        /*const response = await PersistanceManager.doGet("testConnection.php");

        console.log(response.message);*/
        console.log("username: ", ret);
        console.log("passwd: ", passwd);
                
    }

    return <div className="login-page">
        <div className="login-container">
            <div className="login-title">
                Login
            </div>
            <div className="login-form">
                <TextField variant="outlined" label="Username" onChange={(name) => setUsername(name.target.value)}/>
                <TextField variant="outlined" label="Password" type="password" onChange={(passwd) => setPasswd(passwd.target.value)}/>
            </div>
            <div>
                <Button variant="outlined" onClick={() => testConnection()}>Test Connection</Button> 
            </div>
        </div>
    </div>
}

export default Login;
