class ExceptionManager {
    #errorMessage;

    constructor() {
        this.#errorMessage = "";
    }

    getErrorList() {
        return this.#errorMessage;
    }

    addError(message) {
        this.#errorMessage = message;
    }

    clearError() {
        this.#errorMessage = "";
    }
}

export default ExceptionManager;
