import "../css/login/login.css";
import {TextField, Button } from '@mui/material';
import PersistanceManager from "../persistance/PersistanceManager";
import ResultHandler from "../persistance/ResultHandler";

const Login = () => {

    const testConnection = async () => {
        /*const handler = (data) => {
            console.log(data);
        }
        const result = new ResultHandler(handler);
        const response = await PersistanceManager.doGet(result);*/

        const result = await fetch("http://localhost/php/testConnection.php", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });
        const data = await result.json();
        console.log(data.message);
        
    }

    return <div className="login-page">
        <div className="login-container">
            <div className="login-title">
                Login
            </div>
            <div className="login-form">
                <TextField variant="outlined" label="Username"/>
                <TextField variant="outlined" label="Password" type="password"/>
            </div>
            <div>
                <Button variant="outlined" onClick={() => testConnection()}>Test Connection</Button> 
            </div>
        </div>
    </div>
}

export default Login;
