import LogicManager from "../businesslogic/LogicManager";
import {Alert} from '@mui/material';
import CheckIcon from '@mui/icons-material/Check';
import '../css/error/error.css' 


const Error = () => {
    const message = LogicManager.getInstance().getExceptionManager().getErrorList();

    const messageComponent = message.map(item => <Alert icon={<CheckIcon fontSize="inherit" />} severity="success">
        {item}
    </Alert>)

    return (<div className="error-container">
        {message.length && messageComponent}
    </div>)
}

export default Error;
