import LogicManager from "../businesslogic/LogicManager";
import { useState, useEffect } from "react";


const SettingPage = ({setError}) => {
    const [userList, setUserList] = useState([]);
    const [loading, setLoading] = useState(true);

    const loadUsers = async () => {
        const ret = await LogicManager.getInstance().getUserManager().loadUserList();
        if (ret.length == 0) {
            setError(1);
        }
        setUserList(ret);
        setLoading(false);
    }

    useEffect(() => {
        loadUsers();
    }, []);

    return <div>
        <div className='usersList'>
            {loading ? <div>Caricamento</div> : userList.map(usr => (<div key={usr.getId()}>
                <div>{usr.getUserName()}</div>
                <div>vediamo {usr.getIsAdmin() ? "Amministratore" : "Non amministratore"}</div>
            </div>))}
        </div>
    </div>
}

export default SettingPage;
