import "../css/login/login.css";
import {TextField, Button } from '@mui/material';
import PersistanceManager from "../persistance/PersistanceManager";
import { useState } from "react";

const Login = () => {

    const [username, setUsername] = useState("");
    const [passwd, setPasswd] = useState("");

    const testConnection = async () => {
        /*const response = await PersistanceManager.doGet("testConnection.php");

        console.log(response.message);*/
        console.log("username: " + username + "\npasswd: " + passwd);
        
    }

    return <div className="login-page">
        <div className="login-container">
            <div className="login-title">
                Login
            </div>
            <div className="login-form">
                <TextField variant="outlined" label="Username"/>
                <TextField variant="outlined" label="Password" type="password"/>
            </div>
            <div>
                <Button variant="outlined" onClick={() => testConnection()}>Test Connection</Button> 
            </div>
        </div>
    </div>
}

export default Login;
