import User from "./User";

class UserManager {
    #currentUser = null;

    async doLogin(username, passwd) {
        this.#currentUser = await User.doLogin(username, passwd);
        return this.#currentUser;
    }

    async doLogout() {
        let ret = false;
        if ( User.doLogout()){
            this.#currentUser = null;
            ret = true;
        }
        return ret;
    }
}

export default UserManager;
