import { useState } from "react";
import './App.css';
import Error from './error/error.js';
import Login from './login/login.js'
import NavBar from './navbar/NavBar.js';
import LogicManager from "./businesslogic/LogicManager.js";


function App() {
  const [error, setError] = useState([]);
  console.log("prima " + error);

  const refreshError = (option) => {    
    if (option === 1) {
      const newError = LogicManager.getInstance().getExceptionManager().getError();

      if (newError !== ""){
        console.log("err " + error);
        setError(prev => [...prev, newError]);
      }
    }
    else if (option === -1) {
      setError(prev => prev.slice(error.length));
    }
    else{}
  } 

  return (
    <div className="App">
      <header>
        <NavBar/>
      </header>
      {(error && error.length > 0) && <Error message={error} setError={(err) => refreshError(err)}/>}
      <Login error={error} setError={(err) => refreshError(err)}/>
    </div>
  );
}

export default App;
