import "../css/login/login.css";
import {TextField, Button } from '@mui/material';
import { useState } from "react";
import LogicManager from "../businesslogic/LogicManager";


const Login = (setError) => {

    const [username, setUsername] = useState("");
    const [passwd, setPasswd] = useState("");

    const testConnection = async () => {
        try {
            const user = await LogicManager.getInstance().getUserManager().doLogin(username, passwd);
            console.log(user.getUserName());
        } catch (UserLogicException) {
            /*for (let iter of LogicManager.getInstance().getExceptionManager().getErrorList()){
                console.log(iter);
                
            }*/
           setError(true);
        }
    }

    return <div className="login-page">
        <div className="login-container">
            <div className="login-title">
                Login
            </div>
            <div className="login-form">
                <TextField variant="outlined" label="Username" onChange={(name) => setUsername(name.target.value)}/>
                <TextField variant="outlined" label="Password" type="password" onChange={(passwd) => setPasswd(passwd.target.value)}/>
            </div>
            <div>
                <Button variant="outlined" onClick={() => testConnection()}>Test Connection</Button> 
            </div>
        </div>
    </div>
}

export default Login;
