import { useState } from "react";

const NewUserModal = () => {
    const [userName, setUsername] = useState('');
    const [userEmail, setUserEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isAdmin, setIsAdmin] = useState(false);


    return (
        <div className="newuser-modalcontainer">
            <div className="modal-title">
                <h2>Crea un nuovo utente</h2>
            </div>
            <div className="form-container">
                <div className="form-elem">
                    <div>Nome utente</div>
                    <input type="text" onChange={(e) => setUsername(e.target.value)}/>
                </div>
                <div className="form-elem">
                    <div>Email utente</div>
                    <input type="text" onChange={(e) => setUserEmail(e.target.value)}/>
                </div>
                <div className="form-elem">
                    <div>Password utente</div>
                    <input type="text" onChange={(e) => setPassword(e.target.value)}/>
                </div>
                <div className="form-elem">
                <label>Ruolo:</label>
                    <button 
                        onClick={() => setIsAdmin(prev => !prev)} 
                        style={{
                            padding: "10px 20px",
                            background: isAdmin ? "green" : "gray",
                            color: "white",
                            border: "none",
                            borderRadius: "20px",
                            cursor: "pointer"
                        }}
                    >
                        {isAdmin ? "Amministratore" : "Utente normale"}
                    </button>
                    <p>Stato: {isAdmin ? "Amministratore" : "Utente normale"}</p>
                </div>
            </div>
        </div>
    )
}

export default NewUserModal;
