import LogicManager from "../businesslogic/LogicManager";
import { useState, useEffect } from "react";
import '../css/modal/userinfomodal.css';


const UserInfoModal = ({ id, setModalVisible }) => {
    const [userInfo, setUserInfo] = useState();
    const [modifica, setModifica] = useState(false);
    const [email, setEmail] = useState('');
    const [isAdmin, setIsAdmin] = useState(false);
    const [attivato, setAttivato] = useState(false);

    const updateUserInfo = async () => {
        // modifica le informazioni dell'utente
        console.log('informazioni modificate');
        
    }

    const loadInfo = async () => {
        const ret = await LogicManager.getInstance().getUserManager().loadUserInfo(id);

        if (ret) {
            setUserInfo(ret);
        }
    }

    useEffect(() => {
        loadInfo();
    }, []);

    return (
        <div className='userinfo-container'>
            <div className="userinfo-title">
                {userInfo!== undefined ? userInfo.getUserName() : "Caricamento..."}
            </div>
            <div className="userinfo-card">
                <div className="user-info">
                    <label>Email: </label>
                    {modifica ? <input type="text" value={userInfo.getEmail()} onChange={(e) => setEmail(e.target.value) }/> : <p>{userInfo ? (userInfo.getEmail() === '' ? 'Nessuna email' : userInfo.getEmail()) : "Caricamento..."}</p>}
                </div>
                <div className="user-info">
                    <label>Ruolo utente: </label>
                    {modifica ? <input type="text"/> : <p>{userInfo ? (userInfo.getIsAdmin() ? 'Amministratore' : 'Utente') : "Caricamento..."}</p>}
                </div>
                <div className="user-info">
                    <label>Stato utente: </label>
                    <p>{userInfo ? (userInfo.getAttivato() ? 'Attivo' : 'Disattivato') : "Caricamento..."}</p>
                </div>
            </div>
            <div className='button-container'>
                <div className='cancel-btn' onClick={() => setModalVisible(false)}>Annulla</div>
                <div className='edit-btn' onClick={() => {
                    if (modifica) {
                        updateUserInfo();
                    }
                    else {
                        setModifica(true);
                    }
                }}>{modifica ? 'Salva' : 'Modifica'}</div>
            </div>
        </div>
    )
}

export default UserInfoModal;
