import LogicManager from "../businesslogic/LogicManager";
import React, { useState } from "react";
import { Alert } from "@mui/material";
import { v4 as uuidv4 } from "uuid";

const Error = () => {
    const [messages, setMessages] = useState([]);

    const addError = (errorText) => {
        const errorObj = { id: uuidv4(), text: errorText };

        setMessages(prevMessages => [...prevMessages, errorObj]);

        // Rimuove l'errore dopo 2 secondi
        setTimeout(() => {
            setMessages(prevMessages => prevMessages.filter(msg => msg.id !== errorObj.id));
        }, 2000);
    };

    return (
        <div className="error-container">
            {messages.map(({ id, text }) => (
                <Alert key={id} severity="error">
                    {text}
                </Alert>
            ))}
        </div>
    );
};

export default Error;
