import React from "react";
import { Alert, Stack } from "@mui/material";
import CheckIcon from "@mui/icons-material/Check";
import LogicManager from "../businesslogic/LogicManager";

const Error = () => {
    const message = LogicManager.getInstance().getExceptionManager().getErrorList();

    return (
        <Stack spacing={2} className="error-container" sx={{ maxWidth: 500, margin: "auto" }}>
            {message.length > 0 ? (
                message.map((item, index) => (
                    <Alert
                        key={index}
                        icon={<CheckIcon fontSize="inherit" />}
                        severity="success"
                        sx={{ fontSize: "1rem", alignItems: "center" }}
                    >
                        {item}
                    </Alert>
                ))
            ) : (
                <Alert severity="info">Nessun errore presente.</Alert>
            )}
        </Stack>
    );
};

export default Error;
